CREATE TABLE `tbl_user` (`user_id` BIGINT NOT NULL AUTO_INCREMENT,`user_username` VARCHAR(45) NOT NULL,`user_password` VARCHAR(45) NOT NULL,PRIMARY KEY (`user_id`));
CREATE TABLE `tbl_exercise` (`exercise_id` BIGINT NOT NULL AUTO_INCREMENT,`exercise_name` VARCHAR(45) NOT NULL,PRIMARY KEY (`exercise_id`));
CREATE TABLE `tbl_workout` (`workout_id` BIGINT NOT NULL AUTO_INCREMENT,`workout_start` DATETIME NOT NULL,`workout_conclusion` DATETIME NOT NULL,`description` VARCHAR(150) NOT NULL,`intensity` VARCHAR(6) NOT NULL,`user_id` BIGINT NOT NULL,`exercise_id` BIGINT NOT NULL,PRIMARY KEY (`workout_id`),FOREIGN KEY (`user_id`) REFERENCES tbl_user(`user_id`),FOREIGN KEY (`exercise_id`) REFERENCES tbl_exercise(`exercise_id`));